INSERT INTO `service` (`id`, `title`, `body`, `body_short`) VALUES
(1, ' المحاسبة القضائية  ', '<p dir=\"RTL\" style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#999999\">لقد ظهرت في مجال الفقه المحاسبي محاولات عدة لتطوير المحاسبة والمعلومات المحاسبية وكان أبرز تلك التي نادت بالربط بين&nbsp;المحاسبة والقانون من جهة وبين احتياجات&nbsp;القضاء من المعلومات المحاسبية&nbsp;من جهة أخرى. من هنا&nbsp;أطلقت الحاجة إلى خدمات المحاسبة القضائية، وهذا&nbsp;المفهوم الجديد&nbsp;يختلف عن المحاسبة القانونية، وعن التدقيق الخارجي الذي تمارسه الأجهزة الحكومية الرسمية أو مكاتب مراقبي الحسابات والمحاسبين القانونيين&nbsp;في القطاع الخاص، فمخرجات المحاسبة القضائية تقارير ترشد القضاة في إصدار الأحكام بحق الأطراف المتنازعة حول القضايا المالية. </span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#999999\">نحن نمتلك خبرة عملية طويلة لدى إدارة الخبراء في وزارة العدل والتي تؤهلنا للقيام بالدعم المحاسبي للقضايا المنظورة أمام إدارة الخبراء بوزارة العدل من خلال خدمات الدعم القضائي والتي يكون دورنا كخبراء استشاريين، والخدمات التحقيقية والتي نستخدم فيها مهاراتنا المحاسبية لتقديم تقرير كشاهد خبير في المحكمة.</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#999999\">نقوم بعملنا من خلال التعاقد مع مكاتب المحاماة و/او أحد أطراف النزاع لاستكمال أحكام الدفوع من خلال التصدي للجوانب المالية في الدعوى باحترافية عالية وتمثيل أحد أطراف الدعوى أمام إدارة الخبراء لمتابعة سير القضية والتأكد من صحة الردود المحاسبية ومعاييرها من قبل مكتب المحامي لتفادي فوات الحق من خلال دعم مكاتب المحاماة و/او أطراف النزاع بالجوانب المالية والتعاطي معها بمفهوم الخبرة العملية والاحتراف المهني من الاتي:</span></span></span></p>\r\n\r\n<p dir=\"RTL\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#999999\"><strong>أولا: تقديم الخدمات الاستشارية إلي المستشار القانوني في المجالات التالية<span dir=\"LTR\">:</span></strong><br />\r\nأ- الفحص لغرض الاكتشاف<span dir=\"LTR\">.</span><br />\r\nب- التخطيط للمحاكمة<span dir=\"LTR\">.</span><br />\r\nج- الإقرارات<span dir=\"LTR\">.</span><br />\r\nد- إعداد المستندات المطلوبة<span dir=\"LTR\">.</span><br />\r\nه- المناقشة الانتقادية لتقارير الخبراء الآخرين<span dir=\"LTR\">.</span><br />\r\nو- فحص شهادة الشهود<span dir=\"LTR\">.</span><br />\r\nي- مناقشة التسويات<span dir=\"LTR\">.</span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#999999\"><strong>ثانيا: الدعم القضائي&nbsp; &nbsp;<span dir=\"LTR\">. (Litigation Support)</span></strong><br />\r\nأ - تقديم المساعدة المحاسبية في إعداد المرافعات القانونية، ويتضمن ذلك الشكوى الأولية، والطلب علي إنتاج السجلات والتسجيلات اللاحقة<span dir=\"LTR\">.</span><br />\r\nب - تقديم خدمات الاستشارات المحاسبية في الدعوة القضائية أو الدعوى اللاحقة<span dir=\"LTR\">.</span><br />\r\nج - المساعدة في تنظيم السجلات للاستكشاف<span dir=\"LTR\">.</span><br />\r\nد - مساعدة المحامي في المحكمة، وتلاوة الشهادة في المحكمة أو جلسة الاستماع<span dir=\"LTR\">.</span><br />\r\nه - تقديم الدعم بعد المحاكمة، مثل إدارة أموال التسوية</span></span></span></p>\r\n', 'لقد ظهرت في مجال الفقه المحاسبي محاولات عدة لتطوير المحاسبة والمعلومات المحاسبية وكان أبرز تلك التي نادت بالربط بين المحاسبة والقانون من جهة وبين احتياجات القضاء من المعلومات المحاسبية من جهة أخرى. من هنا أطلقت الحاجة إلى خدمات المحاسبة القضائية، '),
(2, 'الاستشارات ', '<div dir=\"RTL\" style=\"margin-left:6px; text-align:justify\"><span style=\"color:#95a5a6\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">إن المحاسبة لم تعد مجرد تسجل للأحداث المالية&nbsp;في السجلات المنتظمة باستخدام الحاسوب أو الأسلوب اليدوي، وإذا&nbsp;أصبحت مهمتها&nbsp;الأساسية مواجهة التحديات الكبيرة التي قد تعيق&nbsp;المحاسبين في توفير المعلومات المفيدة&nbsp;لاتخاذ القرارات على مختلف المستويات، وبما أن النظم المحاسبية&nbsp;تساعد الوحدات بتحقيق أهدافها&nbsp;فان كفاءة وفاعلية&nbsp;هذة&nbsp;النظم تنعكس على كفاءة الوحدات وتتحدد بمدى تحقيق الاهداف&nbsp;والإفصاح والشفافية والصدق والأمانة، فهى بمثابة مرآة تعكس النشاط المحاسبي ونتائجه.</span></span></span></div>\r\n\r\n<div dir=\"RTL\" style=\"margin-left:6px; text-align:justify\"><span style=\"color:#95a5a6\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">من هنا ظهرت أهمية الاستشارات المبينة على أساس التحليل النقدي للقوائم المالية واعمال الفحص النافي للجهالة في اتخاذ القرارات من قبل مجالس الادارة للشركات والمستثمرين </span></span></span></div>\r\n\r\n<div dir=\"RTL\" style=\"margin-left:6px; text-align:justify\">&nbsp;</div>\r\n\r\n<div dir=\"RTL\" style=\"margin-left:6px; margin-right:80px; text-align:center\"><span style=\"font-size:20px\"><span style=\"color:#95a5a6\"><u><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">القراءة النقدية للبيانات المالية</span></strong></u></span></span></div>\r\n\r\n<div dir=\"RTL\" style=\"margin-left:6px; margin-right:80px; text-align:center\">&nbsp;</div>\r\n\r\n<div dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#999999\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>أولا: مفهوم القراءة النقدية للبيانات التالية: -</strong></span></span></span></div>\r\n\r\n<div dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\">&nbsp;</div>\r\n\r\n<div dir=\"rtl\" style=\"text-align:right\"><span style=\"color:#95a5a6\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">تعرف بانها دراسة انتقادية محاسبية ومالية ومستقبلية للقوائم المالية لمنشأة ما، مع الاستعانة بالبيانات الاخرى الضرورية التي لا تشملها تلك القوائم، وذلك باستخدام الاساليب المتنوعة للتحليل، بغرض توضيح اسباب ما حدث من نتائج وتقييم تلك النتائج وبيان الارتباطات بين عناصرها المختلفة، وتتبع تطورها على مدار الفترات المالية المتتالية، واجراء المقارنة على اساس سليم بين المشروعات المتماثلة.</span></span></span></div>\r\n\r\n<div dir=\"rtl\" style=\"text-align:right\">&nbsp;</div>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#999999\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>ثانيا: خصائص القراءة النقدية: -</strong></span></span></span></p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">تعتمد على القوائم المالية ومصادر اخرى كأساس للدراسة وهي تتضمن بيانات متعلقة بالماضي</span></span></span></li>\r\n	<li><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">ان القوائم المالية يلزم اعادة تبويبها بطريقة ملائمة تسمح بإمكانية القيام بالتحليل.</span></span></span></li>\r\n	<li><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">ان هناك العديد من الاساليب التي يمكن الاعتماد عليها عند تحليل القوائم المالية.</span></span></span></li>\r\n	<li><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">انه من الجائز القيام بالتحليل على مستوى المؤسسة خلال سلسة زمنية، او بين المؤسسات المتشابهة في الصناعة الواحدة.</span></span></span></li>\r\n	<li><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">ان التحليل والتقييم لا يقتصر على حساب المؤشرات والنسب وانما يسعى في البحث الى ما وراء هذه المؤشرات والنسب من مدلولات يمكن ان تفيد في عملية اتخاذ القرارات</span></span></span></li>\r\n</ul>\r\n\r\n<div dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#999999\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>ثالثا: اهداف القراءة النقدية للبيانات المالية: -</strong></span></span></span></div>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">تقييم الاداء المحاسبي للمنظمة في الفترة المالية الماضية واكتشاف أي ملاحظات لها انعكاسات على الاداء المالي</span></span></span></div>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">تقيم الاداء المالي للمنظمة في الفترة المالية الماضية</span></span></span></div>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">الكشف عن جوانب الضعف والقصور المالي وتشخيصها تمهيدا لإعادة الهيكلة.</span></span></span></div>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">اتخاذ العديد من القرارات المستقبلية لتحسين الاداء المالي للمنظمة.</span></span></span></div>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">امكانية مقارنة الاداء المالي للمنظمة مع الاداء المالي للمنظمات الأخرى</span></span></span></div>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<div>&nbsp;</div>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:center\"><span style=\"font-size:20px\"><span style=\"color:#7f8c8d\"><u><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>الفحص النافي للجهالة</strong></span></u></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:justify\"><span style=\"color:#95a5a6\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">الفحص النافي للجهالة&nbsp;عملية لابد ان يقوم بها المستثمرون في حالات الاندماج بين الشركات أو الاقدام على الاستحواذ على شركة أخرى، ولابد من ان يقوم به فريق ذو خبرة كبيرة بعمليات الفحص النافي للجهالة، ويشمل هذا النشاط كل ما يتعلق بما يجب ان يعرفه المشترى قبل اتخاذ قرار الشراء ويشمل بنود عديدة تختلف باختلاف النشاط ومنها على سبيل المثال لا الحصر<span dir=\"LTR\">:</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"font-size:16px\"><span style=\"color:#95a5a6\"><u><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>الفحص المالي النافي للجهالة</strong></span></u></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:justify\"><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">يتضمن هذا الفحص تحليل ميزانيات ثلاث سنوات سابقة من حيث هوامش الأرباح وملائمة راس المال العامل والرافعة التشغيلية والمالية ومعدلات الدوران ونسب السيولة الحالية والنقدية والعائد على الاستثمار وغيرها من فنيات التحليل المالي ومقارنة كل هذه المؤشرات بالنسب القياسية ونسب القطاع والشركات المنافسة، وكذلك المخطط المالي لخمس سنوات قادمة من حيث تحليل التكاليف الرأسمالية والتكاليف الثابتة والمتغيرة والقروض طويلة وقصيرة الاجل ونظام الرقابة الداخلية وغير ذلك من المؤشرات المالية المتعلقة بمجال العمل<span dir=\"LTR\">.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><u><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>الفحص الإداري النافي للجهالة</strong></span></span></u></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">يتعلق هذا الفحص بكل ما يتعلق بمصروفات التشغيل والتي تشمل المصروفات الإدارية والعمومية والتسويقية للتأكد من ان جميع المصروفات مسجله في القوائم المالية بشكل صحيح، أهمية هذا الجزء يتمثل في انه يبين للمستثمر حجم ونوعية التكاليف التي يتحملها في حالة ما إذا أراد التوسع في المشروع او إضافة خط انتاج جديد<span dir=\"LTR\">.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><u><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>فحص الأصول النافي للجهالة</strong></span></span></u></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">يلعب هذا الفحص دورا كبيرا في نوعية الشركات كثيفة راس المال وهو فحص كل ما يتعلق بالأصول الثابتة ومعدلات الاهلاك ومواعيد الاحلال والتجديد وما يتعلق بالتأجير التمويلي وكذلك يشمل هذا البند الأصول العقارية والرهن العقاري<span dir=\"LTR\">.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><u><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>فحص الموارد البشرية النافي للجهالة</strong></span></span></u></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">يكون فحص الموارد البشرية مهما جدا في حالة الشركات كثيفة العمالة، ويشمل تحليل اعداد العمال ومعدلات الأجور وكل المزايا التي يتمتع بها العاملون وسياسات إدارة الموارد البشرية ومدى تأثير نقص العمالة على أداء الشركة وكذلك نسبة الأسهم المخصصة للعاملين في الشركة<span dir=\"LTR\">.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><u><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>الفحص البيئي النافي للجهالة</strong></span></span></u></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">يقوم الفحص البيئي على فحص مدى الاضرار التي تسببها الشركة على البيئة والذي يمكن ان يتسبب في غرامات تفرضها السلطات على الشركة وكذلك عزوف المستثمرين عن الاستثمار في الشركات التي تسبب اضرارا للبيئة<span dir=\"LTR\">.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><u><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>الفحص الضريبي النافي للجهالة</strong></span></span></u></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">في الفحص الضريبي يتم فحص الالتزامات الضريبية وطرق حساب الضرائب واي معلقات تخص السلطات الضريبية وكافة المستندات المتعلقة بذلك<span dir=\"LTR\">.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><u><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>فحص الملكية الفكرية النافي للجهالة</strong></span></span></u></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">يتم فحص الملكية الفكرية والعلامة التجارية وغير ذلك من الأصول غير الملموسة هي التي تميز الشركات عن منافسيها وتمتد الشركة بقيمة مضافة لمبيعاتها<span dir=\"LTR\">.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><u><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>الفحص القانوني النافي للجهالة</strong></span></span></u></span>&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">يتضمن الفحص القانوني محاضر مجلس الإدارة والجمعيات العمومية وكذلك الشركات الشقيقة وعقود القروض البنكية والتراخيص المتعلقة بالتشغيل</span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span dir=\"LTR\" style=\"font-size:10.0pt\">.</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><u><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>فحص العملاء النافي للجهالة</strong></span></span></u></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:6px; margin-right:-6px; text-align:right\"><span style=\"color:#95a5a6\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">يتم في فحص العملاء فحص كل ما يتعلق بالعملاء من حيث بيان أكبر العملاء والتسهيلات الائتمانية الممنوحة للعملاء وجودة الخدمة المقدمة لهم<span dir=\"LTR\">.</span></span></span></span></p>\r\n', 'إن المحاسبة لم تعد مجرد تسجل للأحداث المالية في السجلات المنتظمة باستخدام الحاسوب أو الأسلوب اليدوي، وإذا أصبحت مهمتها الأساسية مواجهة التحديات الكبيرة التي قد تعيق المحاسبين في توفير المعلومات المفيدة لاتخاذ القرارات على مختلف المستويات.'),
(3, 'دراسات الجدوى الإقتصادية ', '<div dir=\"RTL\" style=\"margin-right:43px; text-align:justify\"><span style=\"color:#95a5a6\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">تهتم دراسة الجدوى بالفوائد التي تجنى من النظام وبتكاليف تطويره وتشغيله، إذ يتم فيها تقليل الكلفة المتوقعة من هذا النظام، أو العائدات الإضافية، أو الأرباح، إضافة إلى أية فوائد أخرى قد تنجم عن استخدام النظام المقترح.</span></span></span></div>\r\n\r\n<div dir=\"RTL\" style=\"margin-right:43px; text-align:justify\">&nbsp;</div>\r\n\r\n<div dir=\"RTL\" style=\"margin-right:43px; text-align:justify\"><span style=\"color:#95a5a6\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">ولتقييم الفرص الاستثمارية من المشروع، فإن المؤسسة تلجأ إلى استخدام الأساليب التي اعتادت استخدامها، والتي تعتمد على فكرة تحليل العلاقة بين المنافع والتكاليف. </span></span></span></div>\r\n\r\n<div dir=\"RTL\" style=\"margin-right:43px; text-align:justify\">&nbsp;</div>\r\n\r\n<div dir=\"RTL\" style=\"margin-right:43px; text-align:justify\"><span style=\"color:#95a5a6\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">علماً بأنه من الممكن تحديد التكاليف والمنافع من النوع، بينما لا يمكن تحديد قيمة بعضها الآخر، فإذا كانت التكاليف والمنافع من النوع التي يمكن تحديد قيمتها قيل بأنها تكاليف ومنافع ملموسة، وإذا كان من الصعب تحديد قيمتها حالياً، إلا أنها على المدى البعيد، تقود إلى قيم محددة، فإنها تسمى -عندئذ- تكاليف ومنافع غير ملموسة.</span></span></span></div>\r\n\r\n<div dir=\"RTL\" style=\"margin-right:43px; text-align:justify\">&nbsp;</div>\r\n\r\n<div dir=\"RTL\" style=\"margin-right:43px; text-align:justify\">&nbsp;</div>\r\n\r\n<h1 dir=\"RTL\" style=\"margin-right:43px; text-align:center\"><span style=\"color:#7f8c8d\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:11pt\"><span style=\"font-size:16.0pt\">&asymp;&nbsp; اهمية دراسة الجدوى لاصحاب المشاريع المتوسطة والصغيرة&nbsp; &asymp;</span></span></span></strong></span></h1>\r\n\r\n<div dir=\"RTL\" style=\"background:#eeeeee; border:1px solid #cccccc; padding:5px 10px\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">تعدّ دراسة الجدوى الاقتصاديّة من أفضل الأدوات التي يمكن أن يستعين بها المدراء لاتخاذ قرار اقتصادي على مستوى المشروع الخاص فيهم.</span></span></div>\r\n\r\n<div dir=\"RTL\" style=\"background:#eeeeee; border:1px solid #cccccc; padding:5px 10px\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">تمكن من الوصول إلى أفضل إمكانية تخصيص ممكنة للموارد الاقتصادية للمشروع.</span></span></div>\r\n\r\n<div dir=\"RTL\" style=\"background:#eeeeee; border:1px solid #cccccc; padding:5px 10px\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">تساعد الجدوى الاقتصادية على توضيح العوائد التي تعود من المشروع مقارنة بالتكاليف التي تم استثمارها طوال عمر المشروع الافتراضي.</span></span></div>\r\n', 'تهتم دراسة الجدوى بالفوائد التي تجنى من النظام وبتكاليف تطويره وتشغيله، إذ يتم فيها تقليل الكلفة المتوقعة من هذا النظام، أو العائدات الإضافية، أو الأرباح، إضافة إلى أية فوائد أخرى قد تنجم عن استخدام النظام المقترح.');

